import React, { useState } from 'react';  // استيراد React و useState لإدارة الحالة
import authService from '../services/authService';  // استيراد خدمة التوثيق

const SignUpForm = () => {
  const [username, setUsername] = useState('');  // حالة اسم المستخدم
  const [email, setEmail] = useState('');  // حالة البريد الإلكتروني
  const [password, setPassword] = useState('');  // حالة كلمة المرور
  const [error, setError] = useState('');  // حالة الخطأ

  // دالة لمعالجة تقديم نموذج التسجيل
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const userData = { username, email, password };
      const data = await authService.registerUser(userData);  // إرسال بيانات التسجيل إلى خدمة التوثيق
      console.log('User registered:', data);  // هنا يمكن التعامل مع بيانات المستخدم
    } catch (err) {
      setError('Registration failed');  // إذا فشل التسجيل، إظهار رسالة خطأ
    }
  };

  return (
    <div className="sign-up-form">
      <h2 className="title">Sign up</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-field">
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}  // تحديث حالة اسم المستخدم
          />
        </div>
        <div className="input-field">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}  // تحديث حالة البريد الإلكتروني
          />
        </div>
        <div className="input-field">
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}  // تحديث حالة كلمة المرور
          />
        </div>
        <input type="submit" value="Sign up" className="btn" />
        {error && <p className="error">{error}</p>}  // إظهار رسالة الخطأ إذا كانت موجودة
      </form>
    </div>
  );
};

export default SignUpForm;
