import React, { useState } from 'react';  // استيراد React و useState لإدارة الحالة
import authService from '../services/authService';  // استيراد خدمة التوثيق

const SignInForm = () => {
  const [username, setUsername] = useState('');  // حالة اسم المستخدم
  const [password, setPassword] = useState('');  // حالة كلمة المرور
  const [error, setError] = useState('');  // حالة الخطأ

  // دالة لمعالجة تقديم نموذج تسجيل الدخول
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const userData = { username, password };
      const data = await authService.loginUser(userData);  // إرسال بيانات تسجيل الدخول إلى خدمة التوثيق
      console.log('User logged in:', data);  // هنا يمكن التعامل مع بيانات المستخدم
    } catch (err) {
      setError('Invalid credentials');  // إذا فشل تسجيل الدخول، إظهار رسالة خطأ
    }
  };

  return (
    <div className="sign-in-form">
      <h2 className="title">Sign in</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-field">
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}  // تحديث حالة اسم المستخدم
          />
        </div>
        <div className="input-field">
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}  // تحديث حالة كلمة المرور
          />
        </div>
        <input type="submit" value="Login" className="btn solid" />
        {error && <p className="error">{error}</p>}  // إظهار رسالة الخطأ إذا كانت موجودة
      </form>
    </div>
  );
};

export default SignInForm;
